import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Dashboard.css"; // Le CSS pro sobre est ici

function Dashboard({ onLogout }) {
  const [targetUrl, setTargetUrl] = useState("");
  const [reports, setReports] = useState([]);
  const [summaries, setSummaries] = useState([]);
  const [history, setHistory] = useState([]);

  const launchScan = async () => {
    try {
      await axios.post("http://127.0.0.1:8000/scan", { url: targetUrl });
      alert("Scan lancé avec succès !");
      fetchAllData();
    } catch (error) {
      console.error(error);
      alert("Erreur lors du lancement du scan.");
    }
  };

  const fetchAllData = async () => {
    const reportsRes = await axios.get("http://127.0.0.1:8000/reports");
    const summariesRes = await axios.get("http://127.0.0.1:8000/summaries");
    const historyRes = await axios.get("http://127.0.0.1:8000/history");
    setReports(reportsRes.data);
    setSummaries(summariesRes.data);
    setHistory(historyRes.data);
  };

  useEffect(() => {
    fetchAllData();
  }, []);

  const getSeverityLabel = (summary) => {
    const sqli = summary.some((s) => s.includes("SQLi"));
    const xss = summary.some((s) => s.includes("XSS"));
    const ports = summary.some((s) => s.includes("Ports"));

    if (sqli && xss) return <span className="severity critical">Critique</span>;
    if (sqli || xss) return <span className="severity high">Élevée</span>;
    if (ports) return <span className="severity medium">Modérée</span>;
    return <span className="severity low">Faible</span>;
  };

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h1>ShadowSec AI</h1>
        <button onClick={onLogout} className="logout-button">
          Se déconnecter
        </button>
      </header>

      <section className="scan-section">
        <input
          type="text"
          placeholder="http://example.com"
          value={targetUrl}
          onChange={(e) => setTargetUrl(e.target.value)}
          className="url-input"
        />
        <button onClick={launchScan} className="scan-button">
          Lancer un Scan
        </button>
      </section>

      <section className="section">
        <h2>Rapports générés</h2>
        <ul>
          {reports.map((r) => (
            <li key={r.filename}>
              <a href={`http://127.0.0.1:8000/reports/${r.filename}`} download>
                {r.filename}
              </a>
            </li>
          ))}
        </ul>
      </section>

      <section className="section">
        <h2>Résumés des rapports</h2>
        {summaries.map((summary) => (
          <div key={summary.filename} className="summary-block">
            <strong>{summary.filename}</strong>
            <div>{getSeverityLabel(summary.summary)}</div>
            <ul>
              {summary.summary.map((item, idx) => (
                <li key={idx}>{item}</li>
              ))}
            </ul>
          </div>
        ))}
      </section>

      <section className="section">
        <h2>Historique des cibles scannées</h2>
        {history.length === 0 ? (
          <p>Aucun scan effectué pour le moment.</p>
        ) : (
          history.map((item, idx) => (
            <div key={idx} className="history-entry">
              <p>
                <strong>{item.url}</strong> — <em>{item.timestamp}</em>
              </p>
              <a
                href={`http://127.0.0.1:8000/reports/${item.report}`}
                target="_blank"
                rel="noopener noreferrer"
              >
                Voir le rapport
              </a>
            </div>
          ))
        )}
      </section>
    </div>
  );
}

export default Dashboard;
