import React, { useState, useEffect } from "react";
import axios from "axios";

function Dashboard({ onLogout }) {
  const [targetUrl, setTargetUrl] = useState("");
  const [reports, setReports] = useState([]);
  const [summaries, setSummaries] = useState([]);
  const [history, setHistory] = useState([]);

  const launchScan = async () => {
    try {
      await axios.post("http://127.0.0.1:8000/scan", { url: targetUrl });
      alert("‚úÖ Scan lanc√© avec succ√®s !");
      fetchAllData();
    } catch (error) {
      console.error(error);
      alert("‚ùå Erreur lors du lancement du scan.");
    }
  };

  const fetchAllData = async () => {
    const reportsRes = await axios.get("http://127.0.0.1:8000/reports");
    const summariesRes = await axios.get("http://127.0.0.1:8000/summaries");
    const historyRes = await axios.get("http://127.0.0.1:8000/history");
    setReports(reportsRes.data);
    setSummaries(summariesRes.data);
    setHistory(historyRes.data);
  };

  useEffect(() => {
    fetchAllData();
  }, []);

  const renderSummaryTag = (item) => {
    if (item.includes("SQLi")) return <span style={{ color: "cyan" }}>üß® {item}</span>;
    if (item.includes("XSS")) return <span style={{ color: "orange" }}>üî• {item}</span>;
    if (item.includes("Ports")) return <span style={{ color: "violet" }}>üîå {item}</span>;
    if (item.includes("Serveur")) return <span style={{ color: "#ccc" }}>üåê {item}</span>;
    return <span>{item}</span>;
  };

  const getSeverityLabel = (summary) => {
    const sqli = summary.some((s) => s.includes("SQLi"));
    const xss = summary.some((s) => s.includes("XSS"));
    const ports = summary.some((s) => s.includes("Ports"));

    if (sqli && xss) return <span style={{ color: "red", fontWeight: "bold" }}>üü• Critique</span>;
    if (sqli || xss) return <span style={{ color: "orange", fontWeight: "bold" }}>üüß √âlev√©e</span>;
    if (ports) return <span style={{ color: "gold", fontWeight: "bold" }}>üü® Mod√©r√©e</span>;
    return <span style={{ color: "lightgreen", fontWeight: "bold" }}>üü© Faible</span>;
  };

  return (
    <div style={{ padding: 30, fontFamily: "Arial, sans-serif", backgroundColor: "#111", color: "#fff", minHeight: "100vh" }}>
      {/* Header avec logout */}
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
        <h1 style={{ color: "#00FFCC" }}>‚ö° ShadowSec AI Dashboard</h1>
        <button
          onClick={onLogout}
          style={{
            padding: "0.6rem 1rem",
            background: "#ff4d4d",
            color: "#fff",
            fontWeight: "bold",
            border: "none",
            borderRadius: 5
          }}
        >
          üîí Se d√©connecter
        </button>
      </div>

      {/* Formulaire de scan */}
      <div style={{ marginTop: 20 }}>
        <input
          type="text"
          placeholder="http://example.com"
          value={targetUrl}
          onChange={(e) => setTargetUrl(e.target.value)}
          style={{
            padding: "0.6rem",
            width: "350px",
            marginRight: "10px",
            background: "#222",
            color: "#fff",
            border: "1px solid #555"
          }}
        />
        <button
          onClick={launchScan}
          style={{
            padding: "0.6rem 1rem",
            background: "#00FFC6",
            border: "none",
            color: "#000",
            fontWeight: "bold",
            borderRadius: 5
          }}
        >
          üöÄ Lancer un Scan
        </button>
      </div>

      <hr style={{ margin: "30px 0", borderColor: "#333" }} />

      {/* Rapports */}
      <h2>üìÅ Rapports g√©n√©r√©s</h2>
      <ul>
        {reports.map((r) => (
          <li key={r.filename}>
            <a href={`http://127.0.0.1:8000/reports/${r.filename}`} style={{ color: "#00e0ff" }} download>
              üìÑ {r.filename}
            </a>
          </li>
        ))}
      </ul>

      {/* R√©sum√©s */}
      <h2 style={{ marginTop: 30 }}>üß† R√©sum√©s des rapports</h2>
      {summaries.map((summary) => (
        <div key={summary.filename} style={{ marginBottom: 25 }}>
          <strong style={{ color: "#ff00ff" }}>{summary.filename}</strong>
          <div style={{ marginTop: 5 }}>{getSeverityLabel(summary.summary)}</div>
          <ul>
            {summary.summary.map((item, idx) => (
              <li key={idx}>{renderSummaryTag(item)}</li>
            ))}
          </ul>
        </div>
      ))}

      {/* Historique */}
      <h2 style={{ marginTop: 30 }}>üß† Historique des cibles scann√©es</h2>
      {history.length === 0 ? (
        <p>Aucun scan effectu√© pour le moment.</p>
      ) : (
        history.map((item, idx) => (
          <div key={idx} style={{ marginBottom: 10 }}>
            <p>
              <strong style={{ color: "#fff" }}>{item.url}</strong> ‚Äî <em>{item.timestamp}</em>{" "}
              <span style={{ color: "red", fontWeight: "bold" }}> ‚Äî Critique</span>
            </p>
            <a
              href={`http://127.0.0.1:8000/reports/${item.report}`}
              target="_blank"
              rel="noopener noreferrer"
              style={{ color: "#00ffd1" }}
            >
              üîç Voir le rapport
            </a>
          </div>
        ))
      )}
    </div>
  );
}

export default Dashboard;
