import React, { useState } from "react";

function Login({ onLogin }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (username === "admin" && password === "password") {
      onLogin();
    } else {
      alert("Identifiants incorrects.");
    }
  };

  return (
    <div style={{ padding: 40, backgroundColor: "#111", minHeight: "100vh", color: "#fff" }}>
      <h1 style={{ color: "#00FFCC" }}>üîê Connexion ShadowSec AI</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Nom d'utilisateur"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={{ padding: "0.6rem", width: 300, marginBottom: 10, display: "block", background: "#222", color: "#fff" }}
        />
        <input
          type="password"
          placeholder="Mot de passe"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{ padding: "0.6rem", width: 300, marginBottom: 20, display: "block", background: "#222", color: "#fff" }}
        />
        <button
          type="submit"
          style={{
            padding: "0.6rem 1rem",
            background: "#00FFC6",
            color: "#000",
            border: "none",
            borderRadius: 5,
            fontWeight: "bold"
          }}
        >
          Se connecter
        </button>
      </form>
    </div>
  );
}

export default Login;
