// src/Login.js
import React, { useState } from "react";

function Login({ onLogin }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (username === "admin" && password === "password") {
      onLogin(true);
    } else {
      alert("‚ùå Identifiants incorrects !");
    }
  };

  return (
    <div style={{ padding: 50, fontFamily: "Arial", backgroundColor: "#111", color: "#fff", minHeight: "100vh" }}>
      <h1 style={{ color: "#00FFC6" }}>üîê Connexion ShadowSec AI</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Nom d'utilisateur"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={{ padding: 10, marginBottom: 10, width: 250, backgroundColor: "#222", border: "1px solid #555", color: "#fff" }}
        /><br />
        <input
          type="password"
          placeholder="Mot de passe"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{ padding: 10, marginBottom: 20, width: 250, backgroundColor: "#222", border: "1px solid #555", color: "#fff" }}
        /><br />
        <button type="submit" style={{ padding: "10px 20px", backgroundColor: "#00FFC6", color: "#000", fontWeight: "bold", border: "none", borderRadius: 5 }}>
          Se connecter
        </button>
      </form>
    </div>
  );
}

export default Login;
