import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [targetUrl, setTargetUrl] = useState("");
  const [reports, setReports] = useState([]);
  const [history, setHistory] = useState([]);
  const [summaries, setSummaries] = useState([]);
  const [loading, setLoading] = useState(false);

  const launchScan = async () => {
    if (!targetUrl.trim()) return alert("Merci d'entrer une URL valide.");
    setLoading(true);
    try {
      await axios.post("http://127.0.0.1:8000/scan", { url: targetUrl });
      alert("‚úÖ Scan termin√© avec succ√®s !");
      setTargetUrl("");
      fetchAllData();
    } catch (error) {
      console.error(error);
      alert("‚ùå Erreur pendant le scan.");
    }
    setLoading(false);
  };

  const fetchAllData = async () => {
    try {
      const [reportsRes, historyRes, summariesRes] = await Promise.all([
        axios.get("http://127.0.0.1:8000/reports"),
        axios.get("http://127.0.0.1:8000/history"),
        axios.get("http://127.0.0.1:8000/summaries")
      ]);
      setReports(reportsRes.data);
      setHistory(historyRes.data);
      setSummaries(summariesRes.data);
    } catch (err) {
      console.error("Erreur lors du chargement :", err);
    }
  };

  useEffect(() => {
    fetchAllData();
  }, []);

  return (
    <div style={{ padding: "2rem", fontFamily: "Segoe UI", backgroundColor: "#111", color: "#fff", minHeight: "100vh" }}>
      <h1 style={{ color: "#00ffc8" }}>‚ö° ShadowSec AI Dashboard</h1>

      <input
        type="text"
        value={targetUrl}
        onChange={(e) => setTargetUrl(e.target.value)}
        placeholder="http://example.com"
        style={{ padding: "0.6rem", width: "350px", marginRight: "10px", borderRadius: 5, border: "none" }}
      />
      <button
        onClick={launchScan}
        style={{ padding: "0.6rem 1.2rem", backgroundColor: "#00ffc8", color: "#000", border: "none", borderRadius: 5 }}
        disabled={loading}
      >
        üöÄ {loading ? "Scan en cours..." : "Lancer un Scan"}
      </button>

      <hr style={{ margin: "2rem 0", border: "1px solid #444" }} />

      <h2>üìÇ Rapports g√©n√©r√©s</h2>
      <ul>
        {reports.map((r) => (
          <li key={r.filename}>
            <a
              href={`http://127.0.0.1:8000/reports/${r.filename}`}
              target="_blank"
              rel="noopener noreferrer"
              style={{ color: "#00ffc8", textDecoration: "none" }}
            >
              üìÑ {r.filename}
            </a>
          </li>
        ))}
      </ul>

      <h2 style={{ marginTop: "2rem" }}>üß† R√©sum√©s des rapports</h2>
      {summaries.map((s) => (
        <div key={s.filename} style={{ marginBottom: 10, paddingLeft: 10 }}>
          <strong style={{ color: "#ff00ff" }}>{s.filename}</strong>
          <ul>
            {s.summary.map((item, index) => (
              <li key={index}>üîπ {item}</li>
            ))}
          </ul>
        </div>
      ))}

      <h2 style={{ marginTop: "2rem" }}>üß† Historique des cibles scann√©es</h2>
      {history.length === 0 ? (
        <p>Aucun scan effectu√© pour le moment.</p>
      ) : (
        <ul>
          {history.map((h, index) => (
            <li key={index}>
              <strong>{h.url}</strong> ‚Äî {h.timestamp} ‚Äî <span style={{ color: "#ff6666" }}>{h.severity}</span>
              <br />
              <a
                href={`http://127.0.0.1:8000/reports/${h.report}`}
                target="_blank"
                rel="noopener noreferrer"
                style={{ fontSize: "0.9rem", color: "#00ffc8" }}
              >
                üîç Voir le rapport
              </a>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default App;
