import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [targetUrl, setTargetUrl] = useState("");
  const [reports, setReports] = useState([]);
  const [history, setHistory] = useState([]);
  const [summaries, setSummaries] = useState([]);

  const launchScan = async () => {
    if (!targetUrl) return alert("Entre une URL valide.");
    try {
      await axios.post("http://127.0.0.1:8000/scan", { url: targetUrl });
      alert("ðŸš€ Scan lancÃ© !");
      setTargetUrl("");
      fetchReports();
      fetchHistory();
      fetchSummaries();
    } catch (error) {
      console.error(error);
      alert("Erreur lors du scan.");
    }
  };

  const fetchReports = async () => {
    const res = await axios.get("http://127.0.0.1:8000/reports");
    setReports(res.data);
  };

  const fetchHistory = async () => {
    const res = await axios.get("http://127.0.0.1:8000/history");
    setHistory(res.data);
  };

  const fetchSummaries = async () => {
    const res = await axios.get("http://127.0.0.1:8000/summaries");
    setSummaries(res.data);
  };

  useEffect(() => {
    fetchReports();
    fetchHistory();
    fetchSummaries();
  }, []);

  const getSummaryFor = (filename) => {
    const found = summaries.find((s) => s.filename === filename);
    return found ? found.summary.join(" | ") : "RÃ©sumÃ© indisponible.";
  };

  return (
    <div style={{ backgroundColor: "#111", color: "#fff", minHeight: "100vh", padding: "2rem", fontFamily: "Arial" }}>
      <h1 style={{ color: "#00ffcc" }}>âš¡ ShadowSec AI Dashboard</h1>

      <input
        type="text"
        value={targetUrl}
        onChange={(e) => setTargetUrl(e.target.value)}
        placeholder="http://example.com"
        style={{ padding: "0.5rem", width: "300px", marginRight: "1rem" }}
      />
      <button onClick={launchScan} style={{ padding: "0.5rem 1rem", backgroundColor: "#00ffcc", border: "none" }}>
        ðŸš€ Lancer un Scan
      </button>

      <hr style={{ margin: "2rem 0", borderColor: "#333" }} />

      <h2>ðŸ“‚ Rapports gÃ©nÃ©rÃ©s</h2>
      <ul>
        {reports.map((report) => (
          <li key={report.filename}>
            <a
              href={`http://127.0.0.1:8000/reports/${report.filename}`}
              download
              style={{ color: "#00ffcc" }}
            >
              {report.filename}
            </a>
            <br />
            <small>ðŸ§  {getSummaryFor(report.filename)}</small>
          </li>
        ))}
      </ul>

      <hr style={{ margin: "2rem 0", borderColor: "#333" }} />

      <h2>ðŸ§  Historique des cibles scannÃ©es</h2>
      <ul>
        {history.length === 0 ? (
          <p>Aucun scan effectuÃ© pour le moment.</p>
        ) : (
          history.map((entry, i) => (
            <li key={i}>
              <strong>{entry.url}</strong> â€” {entry.timestamp}
            </li>
          ))
        )}
      </ul>
    </div>
  );
}

export default App;
