import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [targetUrl, setTargetUrl] = useState("");
  const [reports, setReports] = useState([]);
  const [summaries, setSummaries] = useState([]);
  const [history, setHistory] = useState([]);

  const launchScan = async () => {
    if (!targetUrl.trim()) {
      alert("Merci d'entrer une URL valide.");
      return;
    }

    try {
      await axios.post("http://127.0.0.1:8000/scan", { url: targetUrl });
      alert("âœ… Scan lancÃ© !");
      fetchReports();
      fetchHistory();
      fetchSummaries();
    } catch (error) {
      console.error("Erreur scan:", error);
      alert("Erreur lors du scan.");
    }
  };

  const fetchReports = async () => {
    const res = await axios.get("http://127.0.0.1:8000/reports");
    setReports(res.data);
  };

  const fetchSummaries = async () => {
    const res = await axios.get("http://127.0.0.1:8000/summaries");
    setSummaries(res.data);
  };

  const fetchHistory = async () => {
    const res = await axios.get("http://127.0.0.1:8000/history");
    setHistory(res.data);
  };

  useEffect(() => {
    fetchReports();
    fetchHistory();
    fetchSummaries();
  }, []);

  return (
    <div style={{ background: "#111", color: "#fff", minHeight: "100vh", padding: "2rem", fontFamily: "Arial" }}>
      <h1>âš¡ <span style={{ color: "springgreen" }}>ShadowSec AI Dashboard</span></h1>

      <input
        type="text"
        placeholder="http://example.com"
        value={targetUrl}
        onChange={(e) => setTargetUrl(e.target.value)}
        style={{ padding: 10, width: 320, marginRight: 10, borderRadius: 4 }}
      />
      <button onClick={launchScan} style={{ padding: 10, backgroundColor: "#00ffc3", border: "none", borderRadius: 4 }}>
        ðŸš€ Lancer un Scan
      </button>

      <hr style={{ margin: "2rem 0" }} />
      <h2>ðŸ“‚ Rapports gÃ©nÃ©rÃ©s</h2>
      <ul>
        {reports.map((r) => (
          <li key={r.filename}>
            <a
              href={`http://127.0.0.1:8000/reports/${r.filename}`}
              target="_blank"
              rel="noreferrer"
              style={{ color: "#00ffc3" }}
            >
              {r.filename}
            </a>
          </li>
        ))}
      </ul>

      <h2>ðŸ§  RÃ©sumÃ©s des rapports</h2>
      {summaries.map((s) => (
        <div key={s.filename} style={{ marginBottom: 15 }}>
          <strong style={{ color: "#ff0" }}>{s.filename}</strong>
          <ul>
            {s.summary.map((line, idx) => (
              <li key={idx}>{line}</li>
            ))}
          </ul>
        </div>
      ))}

      <h2>ðŸ§  Historique des cibles scannÃ©es</h2>
      {history.length === 0 ? (
        <p>Aucun scan effectuÃ© pour le moment.</p>
      ) : (
        <ul>
          {history.map((entry, i) => (
            <li key={i}>
              <strong>{entry.url}</strong> â€” {entry.timestamp} â€” Rapport :{" "}
              <a
                href={`http://127.0.0.1:8000/reports/${entry.report}`}
                style={{ color: "#00ffc3" }}
                target="_blank"
                rel="noreferrer"
              >
                {entry.report}
              </a>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default App;
