import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css"; // si tu veux customiser le style

function App() {
  const [targetUrl, setTargetUrl] = useState("");
  const [reports, setReports] = useState([]);
  const [history, setHistory] = useState([]);
  const [summaries, setSummaries] = useState([]);

  const launchScan = async () => {
    if (!targetUrl.trim()) return alert("Merci d'entrer une URL valide !");
    try {
      await axios.post("http://127.0.0.1:8000/scan", { url: targetUrl });
      alert("âœ… Scan lancÃ© !");
      setTargetUrl("");
      fetchReports();
      fetchHistory();
      fetchSummaries();
    } catch (err) {
      alert("Erreur lors du lancement du scan.");
    }
  };

  const fetchReports = async () => {
    const res = await axios.get("http://127.0.0.1:8000/reports");
    setReports(res.data);
  };

  const fetchHistory = async () => {
    const res = await axios.get("http://127.0.0.1:8000/history");
    setHistory(res.data);
  };

  const fetchSummaries = async () => {
    const res = await axios.get("http://127.0.0.1:8000/summaries");
    setSummaries(res.data);
  };

  useEffect(() => {
    fetchReports();
    fetchHistory();
    fetchSummaries();
  }, []);

  return (
    <div style={{ padding: "30px", fontFamily: "Arial", background: "#111", color: "#fff" }}>
      <h1 style={{ color: "#00FFD1" }}>âš¡ ShadowSec AI Dashboard</h1>

      <div style={{ display: "flex", gap: "10px" }}>
        <input
          type="text"
          placeholder="http://example.com"
          value={targetUrl}
          onChange={(e) => setTargetUrl(e.target.value)}
          style={{ padding: 10, width: 300 }}
        />
        <button onClick={launchScan} style={{ padding: 10, backgroundColor: "#00FFD1", border: "none", borderRadius: 5 }}>
          ðŸš€ Lancer un Scan
        </button>
      </div>

      <hr style={{ margin: "30px 0", borderColor: "#333" }} />

      <h2>ðŸ“‚ Rapports gÃ©nÃ©rÃ©s</h2>
      {summaries.map((s, idx) => (
        <div key={idx} style={{ background: "#222", padding: 15, borderRadius: 8, marginBottom: 10 }}>
          <b>{s.filename}</b>
          <ul>
            {s.summary.map((line, i) => (
              <li key={i}>{line}</li>
            ))}
          </ul>
          <a
            href={`http://127.0.0.1:8000/reports/${s.filename}`}
            target="_blank"
            rel="noopener noreferrer"
            style={{ color: "#00FFD1" }}
          >
            ðŸ“¥ TÃ©lÃ©charger le rapport
          </a>
        </div>
      ))}

      <hr style={{ margin: "30px 0", borderColor: "#333" }} />

      <h2>ðŸ§  Historique des cibles scannÃ©es</h2>
      {history.length === 0 ? (
        <p>Aucun scan effectuÃ© pour le moment.</p>
      ) : (
        <ul>
          {history.map((entry, idx) => (
            <li key={idx}>
              {entry.timestamp} â€“ {entry.url} â†’ <code>{entry.report}</code>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default App;
