import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [targetUrl, setTargetUrl] = useState("");
  const [reports, setReports] = useState([]);
  const [selectedReport, setSelectedReport] = useState("");
  const [reportContent, setReportContent] = useState("");
  const [statusMessage, setStatusMessage] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const launchScan = async () => {
    if (!targetUrl.trim()) {
      setStatusMessage("❗ Merci de saisir une URL cible.");
      return;
    }

    setIsLoading(true);
    setStatusMessage("⏳ Scan en cours...");

    try {
      const response = await axios.post("http://127.0.0.1:8000/scan", {
        url: targetUrl,
      });

      setStatusMessage("✅ Scan terminé avec succès !");
      fetchReports(); // actualise la liste après le scan
    } catch (error) {
      console.error("Erreur lors du scan :", error);
      setStatusMessage("❌ Erreur lors du lancement du scan.");
    } finally {
      setIsLoading(false);
    }
  };

  const fetchReports = async () => {
    try {
      const response = await axios.get("http://127.0.0.1:8000/reports");
      setReports(response.data);
    } catch (error) {
      console.error("Erreur chargement des rapports :", error);
    }
  };

  const viewReport = async (filename) => {
    try {
      const response = await axios.get(
        `http://127.0.0.1:8000/reports/${filename}/content`
      );
      setSelectedReport(filename);
      setReportContent(response.data);
    } catch (error) {
      console.error("Erreur lecture rapport :", error);
      setReportContent("Erreur lors du chargement du rapport.");
    }
  };

  useEffect(() => {
    fetchReports();
  }, []);

  return (
    <div style={{ padding: 30, fontFamily: "Arial, sans-serif" }}>
      <h1>ShadowSec AI Dashboard</h1>

      <input
        type="text"
        placeholder="http://example.com"
        value={targetUrl}
        onChange={(e) => setTargetUrl(e.target.value)}
        style={{ padding: 8, width: 300 }}
      />
      <br />
      <br />
      <button onClick={launchScan} disabled={isLoading}>
        {isLoading ? "Scan en cours..." : "🚀 Lancer un Scan"}
      </button>

      <p style={{ marginTop: 10, fontWeight: "bold", color: "#444" }}>
        {statusMessage}
      </p>

      <hr />
      <h2>📂 Rapports générés</h2>
      <ul>
        {reports.map((report) => (
          <li key={report.filename}>
            <button onClick={() => viewReport(report.filename)}>
              📄 {report.filename}
            </button>
          </li>
        ))}
      </ul>

      {selectedReport && (
        <div
          style={{
            marginTop: "2rem",
            whiteSpace: "pre-wrap",
            background: "#f4f4f4",
            padding: "1rem",
            borderRadius: "8px",
          }}
        >
          <h3>📑 Contenu du rapport : {selectedReport}</h3>
          <p>{reportContent}</p>
        </div>
      )}
    </div>
  );
}

export default App;
