// src/Dashboard.js
import React, { useState, useEffect } from "react";
import axios from "axios";

function Dashboard() {
  const [targetUrl, setTargetUrl] = useState("");
  const [reports, setReports] = useState([]);
  const [summaries, setSummaries] = useState([]);
  const [history, setHistory] = useState([]);

  const launchScan = async () => {
    try {
      await axios.post("http://127.0.0.1:8000/scan", { url: targetUrl });
      alert("✅ Scan lancé avec succès !");
      fetchAllData();
    } catch (error) {
      console.error(error);
      alert("❌ Erreur lors du lancement du scan.");
    }
  };

  const fetchAllData = async () => {
    const reportsRes = await axios.get("http://127.0.0.1:8000/reports");
    const summariesRes = await axios.get("http://127.0.0.1:8000/summaries");
    const historyRes = await axios.get("http://127.0.0.1:8000/history");
    setReports(reportsRes.data);
    setSummaries(summariesRes.data);
    setHistory(historyRes.data);
  };

  useEffect(() => {
    fetchAllData();
  }, []);

  const renderSummaryTag = (item) => {
    if (item.includes("SQLi")) return <span style={{ color: "cyan" }}>🧨 {item}</span>;
    if (item.includes("XSS")) return <span style={{ color: "orange" }}>🔥 {item}</span>;
    if (item.includes("Ports")) return <span style={{ color: "violet" }}>🔌 {item}</span>;
    if (item.includes("Serveur")) return <span style={{ color: "#ccc" }}>🌐 {item}</span>;
    return <span>{item}</span>;
  };

  const getSeverityLabel = (summary) => {
    const sqli = summary.some((s) => s.includes("SQLi"));
    const xss = summary.some((s) => s.includes("XSS"));
    const ports = summary.some((s) => s.includes("Ports"));

    if (sqli && xss) return <span style={{ color: "red", fontWeight: "bold" }}>🟥 Critique</span>;
    if (sqli || xss) return <span style={{ color: "orange", fontWeight: "bold" }}>🟧 Élevée</span>;
    if (ports) return <span style={{ color: "gold", fontWeight: "bold" }}>🟨 Modérée</span>;
    return <span style={{ color: "lightgreen", fontWeight: "bold" }}>🟩 Faible</span>;
  };

  return (
    <div style={{ padding: 30, fontFamily: "Arial, sans-serif", backgroundColor: "#111", color: "#fff", minHeight: "100vh" }}>
      <h1 style={{ color: "#00FFCC" }}>⚡ ShadowSec AI Dashboard</h1>

      <input
        type="text"
        placeholder="http://example.com"
        value={targetUrl}
        onChange={(e) => setTargetUrl(e.target.value)}
        style={{ padding: "0.6rem", width: "350px", marginRight: "10px", background: "#222", color: "#fff", border: "1px solid #555" }}
      />
      <button onClick={launchScan} style={{ padding: "0.6rem 1rem", background: "#00FFC6", border: "none", color: "#000", fontWeight: "bold", borderRadius: 5 }}>
        🚀 Lancer un Scan
      </button>

      <hr style={{ margin: "30px 0", borderColor: "#333" }} />

      <h2>📁 Rapports générés</h2>
      <ul>
        {reports.map((r) => (
          <li key={r.filename}>
            <a href={`http://127.0.0.1:8000/reports/${r.filename}`} style={{ color: "#00e0ff" }} download>
              📄 {r.filename}
            </a>
          </li>
        ))}
      </ul>

      <h2 style={{ marginTop: 30 }}>🧠 Résumés des rapports</h2>
      {summaries.map((summary) => (
        <div key={summary.filename} style={{ marginBottom: 25 }}>
          <strong style={{ color: "#ff00ff" }}>{summary.filename}</strong>
          <div style={{ marginTop: 5 }}>{getSeverityLabel(summary.summary)}</div>
          <ul>
            {summary.summary.map((item, idx) => (
              <li key={idx}>{renderSummaryTag(item)}</li>
            ))}
          </ul>
        </div>
      ))}

      <h2 style={{ marginTop: 30 }}>🧠 Historique des cibles scannées</h2>
      {history.length === 0 ? (
        <p>Aucun scan effectué pour le moment.</p>
      ) : (
        history.map((item, idx) => (
          <div key={idx} style={{ marginBottom: 10 }}>
            <p>
              <strong style={{ color: "#fff" }}>{item.url}</strong> — <em>{item.timestamp}</em>{" "}
              <span style={{ color: "red", fontWeight: "bold" }}> — Critique</span>
            </p>
            <a href={`http://127.0.0.1:8000/reports/${item.report}`} target="_blank" rel="noopener noreferrer" style={{ color: "#00ffd1" }}>
              🔍 Voir le rapport
            </a>
          </div>
        ))
      )}
    </div>
  );
}

export default Dashboard;
