import { useState, useEffect } from "react";

function App() {
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(false);

  const fetchReports = async () => {
    setLoading(true);
    try {
      const res = await fetch("http://localhost:8000/reports");
      const data = await res.json();
      setReports(data);
    } catch (err) {
      console.error("Erreur chargement des rapports", err);
    } finally {
      setLoading(false);
    }
  };

  const launchScan = async () => {
    setLoading(true);
    try {
      const res = await fetch("http://localhost:8000/scan", { method: "POST" });
      const data = await res.json();
      alert(data.message);
      fetchReports();
    } catch (err) {
      alert("Erreur lors du scan");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchReports();
  }, []);

  return (
    <div style={{ padding: "2rem", fontFamily: "sans-serif" }}>
      <h1>ShadowSec AI Dashboard</h1>
      <button onClick={launchScan} disabled={loading} style={{ margin: "1rem 0", padding: "0.5rem 1rem" }}>
        {loading ? "Scan en cours..." : "Lancer un Scan"}
      </button>
      <ul>
        {reports.map((r, i) => (
          <li key={i}>
            <a href={`http://localhost:8000/reports/${r.filename}`} target="_blank" rel="noreferrer">
              {r.filename}
            </a>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
