import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [targetUrl, setTargetUrl] = useState("");
  const [reports, setReports] = useState([]);
  const [selectedReport, setSelectedReport] = useState("");
  const [reportContent, setReportContent] = useState("");
  const [statusMessage, setStatusMessage] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const launchScan = async () => {
    if (!targetUrl.trim()) {
      setStatusMessage("â— Merci de saisir une URL cible.");
      return;
    }

    setIsLoading(true);
    setStatusMessage("â³ Scan en cours...");

    try {
      await axios.post("http://127.0.0.1:8000/scan", { url: targetUrl });
      setStatusMessage("âœ… Scan terminÃ© avec succÃ¨s !");
      fetchReports();
    } catch (error) {
      console.error(error);
      setStatusMessage("âŒ Erreur lors du lancement du scan.");
    } finally {
      setIsLoading(false);
    }
  };

  const fetchReports = async () => {
    try {
      const response = await axios.get("http://127.0.0.1:8000/reports");
      setReports(response.data);
    } catch (error) {
      console.error("Erreur chargement des rapports :", error);
    }
  };

  const viewReport = async (filename) => {
    try {
      const response = await axios.get(
        `http://127.0.0.1:8000/reports/${filename}/content`
      );
      setSelectedReport(filename);
      setReportContent(response.data);
    } catch (error) {
      setReportContent("Erreur lors du chargement du rapport.");
    }
  };

  useEffect(() => {
    fetchReports();
  }, []);

  return (
    <div style={{ backgroundColor: "#121212", minHeight: "100vh", color: "#f0f0f0", padding: 40, fontFamily: "Segoe UI, sans-serif" }}>
      <h1 style={{ color: "#00FFC6" }}>âš¡ ShadowSec AI Dashboard</h1>

      <div style={{ marginBottom: 30 }}>
        <input
          type="text"
          placeholder="http://example.com"
          value={targetUrl}
          onChange={(e) => setTargetUrl(e.target.value)}
          style={{
            padding: 10,
            width: 350,
            borderRadius: 5,
            border: "1px solid #333",
            backgroundColor: "#1e1e1e",
            color: "#fff",
          }}
        />
        <button
          onClick={launchScan}
          disabled={isLoading}
          style={{
            marginLeft: 15,
            padding: "10px 20px",
            backgroundColor: "#00FFC6",
            color: "#000",
            border: "none",
            borderRadius: 5,
            cursor: "pointer",
          }}
        >
          {isLoading ? "Scan en cours..." : "ðŸš€ Lancer un Scan"}
        </button>
        <p style={{ marginTop: 10, fontWeight: "bold" }}>{statusMessage}</p>
      </div>

      <hr style={{ borderColor: "#333" }} />

      <h2>ðŸ“‚ Rapports gÃ©nÃ©rÃ©s</h2>
      <ul style={{ listStyle: "none", padding: 0 }}>
        {reports.map((report) => (
          <li key={report.filename} style={{ marginBottom: 5 }}>
            <button
              onClick={() => viewReport(report.filename)}
              style={{
                background: "#1e1e1e",
                border: "1px solid #333",
                color: "#00FFC6",
                padding: "5px 10px",
                borderRadius: 4,
                cursor: "pointer",
              }}
            >
              ðŸ“„ {report.filename}
            </button>
          </li>
        ))}
      </ul>

      {selectedReport && (
        <div
          style={{
            marginTop: 30,
            background: "#1e1e1e",
            padding: 20,
            borderRadius: 8,
            border: "1px solid #333",
            whiteSpace: "pre-wrap",
          }}
        >
          <h3 style={{ color: "#00FFC6" }}>ðŸ“‘ Rapport : {selectedReport}</h3>
          <p>{reportContent}</p>
        </div>
      )}
    </div>
  );
}

export default App;
