import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [targetUrl, setTargetUrl] = useState("");
  const [message, setMessage] = useState("");
  const [reports, setReports] = useState([]);

  const launchScan = async () => {
    try {
      const response = await axios.post("http://127.0.0.1:8000/scan", {
        url: targetUrl,
      });
      setMessage(response.data.message || "Scan lancÃ© !");
      fetchReports(); // mettre Ã  jour la liste aprÃ¨s le scan
    } catch (error) {
      console.error("Erreur lors du lancement du scan :", error);
      setMessage("Erreur lors du scan.");
    }
  };

  const fetchReports = async () => {
    try {
      const response = await axios.get("http://127.0.0.1:8000/reports");
      setReports(response.data);
    } catch (error) {
      console.error("Erreur lors du chargement des rapports :", error);
    }
  };

  useEffect(() => {
    fetchReports();
  }, []);

  return (
    <div style={{ padding: "2rem", fontFamily: "Arial, sans-serif" }}>
      <h1>ShadowSec AI Dashboard</h1>

      <input
        type="text"
        value={targetUrl}
        onChange={(e) => setTargetUrl(e.target.value)}
        placeholder="http://example.com"
        style={{ padding: "0.5rem", width: "300px" }}
      />
      <br /><br />

      <button onClick={launchScan} style={{ padding: "0.5rem 1rem" }}>
        Lancer un Scan
      </button>

      <p style={{ color: "green", marginTop: "1rem" }}>{message}</p>

      <hr />
      <h2>ðŸ“‚ Rapports gÃ©nÃ©rÃ©s</h2>
      <ul>
        {reports.map((report) => (
          <li key={report.filename}>
            <a
              href={`http://127.0.0.1:8000/reports/${report.filename}`}
              download
              target="_blank"
              rel="noopener noreferrer"
            >
              {report.filename}
            </a>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
